import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
// import IconButton from '@material-ui/core/IconButton';
// import MenuIcon from '@material-ui/icons/Menu';

import './Header.scss';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    position: 'absolute',
    width: '100%',
    marginTop: '25px',
    zIndex: 10,
  },
  appBar: {
    borderRadius: 5,
    boxShadow: '0 2px 20px rgb(0 0 0 / 10%)',
    transition: 'all ease 250ms',
  },
  appBarFull: {
    borderRadius: 0,
    backgroundColor: '#bac866',
    transition: 'all ease 250ms',
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  navLogo: {
    maxWidth: 120,
  },
}));

export default function Header() {
  const classes = useStyles();
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 100,
  });
  return (
    <div className={classes.root}>
      <Container maxWidth="lg">
        <AppBar
          color="default"
          elevation={0}
          className={clsx(classes.appBar, {
            [classes.appBarFull]: trigger,
          })}
          position={trigger ? 'fixed' : 'static'}>
          <Container maxWidth="lg">
            <Toolbar>
              <a href="#home" className="navbar-brand">
                {trigger ? (
                  <img className={classes.navLogo} src="assets/img/white_logo.png" alt="Logo" />
                ) : (
                  <img className={classes.navLogo} src="assets/img/logo.png" alt="Logo" />
                )}
              </a>
              <Typography variant="h6" className={classes.title}>
                News
              </Typography>
              <Button color="inherit">Login</Button>
            </Toolbar>
          </Container>
        </AppBar>
      </Container>
    </div>
  );
}
