import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
// import Box from '@material-ui/core/Box';
// import Typography from '@material-ui/core/Typography';
// import Button from '@material-ui/core/Button';
// import IconButton from '@material-ui/core/IconButton';
// import MenuIcon from '@material-ui/icons/Menu';

import './Header.scss';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    position: 'absolute',
    width: '100%',
    marginTop: '25px',
    zIndex: 10,
  },
  appBar: {
    borderRadius: 5,
    boxShadow: '0 2px 20px rgb(0 0 0 / 10%)',
    transition: 'all ease 250ms',
    maxWidth: 1140,
    margin: '0 auto',
  },
  appBarFull: {
    borderRadius: 0,
    backgroundColor: '#bac866',
    transition: 'all ease 250ms',
    maxWidth: '100%',
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  navbarBrand: {
    padding: 5,
    marginRight: 'auto',
  },
  navbarBrandLogo: {
    maxWidth: 120,
    display: 'block',
  },
  navbarNav: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    display: 'flex',
    justifyContent: 'flex-end',
    height: 70,
    alignItems: 'center',
  },
  navLink: {
    padding: '26px 10px',
  },
}));

export default function Header() {
  const classes = useStyles();
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 20,
  });
  return (
    <div className={classes.root}>
      <Container maxWidth="lg">
        <AppBar
          color="inherit"
          elevation={0}
          className={clsx(classes.appBar, {
            [classes.appBarFull]: trigger,
          })}
          position={trigger ? 'fixed' : 'static'}>
          <Container disableGutters maxWidth="lg">
            <Toolbar>
              <div className="container">
                <nav className="navbar navbar-expand-lg">
                  <div className="navbar-header d-flex align-items-center justify-content-between">
                    <a className="navbar-brand p-0" href="#0">
                      {trigger ? (
                        <img
                          className={classes.navbarBrandLogo}
                          src="assets/img/white_logo.png"
                          alt="Logo"
                        />
                      ) : (
                        <img
                          className={classes.navbarBrandLogo}
                          src="assets/img/logo.png"
                          alt="Logo"
                        />
                      )}
                    </a>
                    <button
                      className="navbar-toggler"
                      type="button"
                      data-toggle="collapse"
                      data-target="#navbarTogglerDemo02"
                      aria-controls="navbarTogglerDemo02"
                      aria-expanded="false"
                      aria-label="Toggle navigation">
                      <span className="navbar-toggler-icon"></span>
                    </button>
                  </div>
                  <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                    <ul className="navbar-nav mr-auto w-100 align-items-center justify-content-end">
                      <li className="nav-item active">
                        <a className="nav-link" href="#0">
                          Home <span className="sr-only">(current)</span>
                        </a>
                      </li>
                      <li className="nav-item">
                        <a className="nav-link" href="#0">
                          Link
                        </a>
                      </li>
                      <li className="nav-item">
                        <a
                          className="nav-link disabled"
                          href="#0"
                          tabIndex="-1"
                          aria-disabled="true">
                          Disabled
                        </a>
                      </li>
                    </ul>
                  </div>
                </nav>
              </div>
            </Toolbar>
          </Container>
        </AppBar>
      </Container>
    </div>
  );
}
