import React from 'react';
import PropTypes from 'prop-types';
import { connect, useSelector } from 'react-redux';
import { compose } from 'redux';
import { Link } from 'react-router-dom';
import { Form, Field } from 'react-final-form';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { faFacebookF } from '@fortawesome/free-brands-svg-icons';
// import { Modal } from 'react-bootstrap';
// import SocialButton from 'components/SocialButton';
import Header from 'components/Header';
import Footer from 'components/Footer';
import * as CONSTANTS from 'common/constants';
import FormStateToRedux from 'common/util/FormStateToRedux';
// import SocialLogin from 'containers/Auth/SocialLogin';
import {
  forgotPassword as forgotPasswordAction,
  isLoadingForgotPassword,
  selectError,
} from 'containers/Auth/authSlice';

function ForgotPassword({ forgotPassword }) {
  const loading = useSelector(isLoadingForgotPassword);
  const error = useSelector(selectError);
  const onSubmit = (values) => {
    // eslint-disable-next-line no-undef
    forgotPassword(values);
  };

  return (
    <>
      <Header />

      {loading ? (
        <div className="loader">
          <img src="assets/img/loader.svg" alt="loader" />
        </div>
      ) : null}

      <section className="login pt-5">
        <div className="container pt-5 pb-4">
          <div className="row align-items-center">
            <div className="col-md-12">
              <div className="form">
                <Form onSubmit={onSubmit} subscription={{ submitting: true, pristine: true }}>
                  {({ handleSubmit, submitting, pristine }) => (
                    <form onSubmit={handleSubmit}>
                      <FormStateToRedux form="FORGOT_PASSOWORD_FORM" />
                      <h2 className="mb-4">Forgot Password?</h2>
                      {error ? (
                        <p className="text-danger">
                          <span>{error}</span>
                        </p>
                      ) : null}
                      <div className="form-group">
                        <label htmlFor="email">Registered Email</label>
                        <Field
                          id="email"
                          type="email"
                          component="input"
                          className="form-control"
                          required
                          placeholder="Email"
                          name="email"
                          pattern="[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,8}$"
                        />
                        <p className="text-danger">
                          <span if="UserEmail.errors.required">Email is required.</span>
                          <span if="UserEmail.errors?.pattern">
                            Please enter a valid email address.
                          </span>
                        </p>
                      </div>

                      <div className="form-group mb-0 mt-4">
                        <button
                          type="button"
                          className="btn btn-theme"
                          disabled={submitting || pristine}>
                          Submit
                        </button>
                      </div>
                      <div className="text-center mt-3">
                        <Link to={CONSTANTS.LOGIN_PAGE}>Back To Login</Link>
                      </div>
                    </form>
                  )}
                </Form>
              </div>
            </div>
          </div>
        </div>
        <div className="login_right">
          <img src="assets/img/sing_up.png" alt="forgot password" />
        </div>
      </section>

      <Footer />
    </>
  );
}

ForgotPassword.propTypes = {
  forgotPassword: PropTypes.func,
};

const mapDispatchToProps = (dispatch) => ({
  forgotPassword: (values) => dispatch(forgotPasswordAction(values)),
});

const withConnect = connect(null, mapDispatchToProps);

export default compose(withConnect)(ForgotPassword);
